import React, { useState, useRef } from 'react';
import { 
  Box, 
  Flex, 
  Heading, 
  Text, 
  Input, 
  Textarea, 
  Button,
  VStack,
  HStack,
  Image,
  useToast
} from '@chakra-ui/react';
import NavigationBar from '../components/NavigationBar.tsx';

// API base URL - can be configured based on environment
const API_BASE_URL = process.env.REACT_APP_API_BASE_URL || 'http://localhost:8000';

function NewDataEntry() {
  const [formData, setFormData] = useState({
    ms_id: '',
    sigla: '',
    date: '',
    other_names: '',
    contents: '',
    place_of_origin: '',
    total_folia: '',
    dimensions: '',
    materials: '',
    laod_folia: '',
    format_description: '',
    transcription: ''
  });
  const [selectedImage, setSelectedImage] = useState<string | null>(null);
  const [isLoading, setIsLoading] = useState(false);
  const fileInputRef = useRef<HTMLInputElement>(null);
  const toast = useToast();

  function handleChange(e: React.ChangeEvent<HTMLInputElement | HTMLTextAreaElement>) {
    const { name, value } = e.target;
    setFormData(function(prev) {
      return {
        ...prev,
        [name]: value
      };
    });
  }

  function handleImageClick() {
    if (fileInputRef.current) {
      fileInputRef.current.click();
    }
  }

  function handleImageChange(e: React.ChangeEvent<HTMLInputElement>) {
    const file = e.target.files?.[0];
    if (file) {
      const reader = new FileReader();
      reader.onload = function(e) {
        setSelectedImage(e.target?.result as string);
      };
      reader.readAsDataURL(file);
    }
  }

  function handleClear() {
    setFormData({
      ms_id: '',
      sigla: '',
      date: '',
      other_names: '',
      contents: '',
      place_of_origin: '',
      total_folia: '',
      dimensions: '',
      materials: '',
      laod_folia: '',
      format_description: '',
      transcription: ''
    });
    setSelectedImage(null);
    if (fileInputRef.current) {
      fileInputRef.current.value = '';
    }
  }

  async function handleSaveAsDraft() {
    try {
      setIsLoading(true);
      const formDataToSend = new FormData();

      // Add the main document data
      formDataToSend.append('document', JSON.stringify({
        filename: `${formData.sigla}.docx`,
        metadata: {
          'MS ID:': formData.ms_id,
          'Other Names:': formData.other_names,
          'Contents:': formData.contents,
          'Date:': formData.date,
          'Origin:': formData.place_of_origin,
          'Total Folia:': formData.total_folia,
          'Dimensions:': formData.dimensions,
          'Materials:': formData.materials,
          'Laod Folia:': formData.laod_folia,
          'Format Description:': formData.format_description
        },
        verses: [] // Empty verses array for draft
      }));

      // Add the image if it exists
      if (selectedImage) {
        // Convert base64 to blob
        const response = await fetch(selectedImage);
        const blob = await response.blob();
        formDataToSend.append('image', blob, 'manuscript_image.jpg');
      }

      const response = await fetch(`${API_BASE_URL}/api/documents/draft/`, {
        method: 'POST',
        body: formDataToSend
      });

      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.error || 'Failed to save draft');
      }

      toast({
        title: 'Draft saved',
        status: 'success',
        duration: 3000,
        isClosable: true,
      });
    } catch (error) {
      toast({
        title: 'Error saving draft',
        description: error instanceof Error ? error.message : 'Unknown error occurred',
        status: 'error',
        duration: 5000,
        isClosable: true,
      });
    } finally {
      setIsLoading(false);
    }
  }

  async function handleComplete() {
    try {
      setIsLoading(true);
      // Validate required fields
      const requiredFields = ['ms_id', 'sigla', 'date'];
      const missingFields = requiredFields.filter(field => !formData[field]);

      if (missingFields.length > 0) {
        throw new Error(`Required fields missing: ${missingFields.join(', ')}`);
      }

      // Process transcription into verses
      // Split by newlines and filter out empty lines
      const verses = formData.transcription
        .split('\n')
        .map((line, index) => ({
          verse_number: index + 1,
          verse_text: line.trim()
        }))
        .filter(verse => verse.verse_text.length > 0);

      const formDataToSend = new FormData();

      // Add the main document data
      formDataToSend.append('document', JSON.stringify({
        filename: `${formData.sigla}.docx`,
        metadata: {
          'MS ID:': formData.ms_id,
          'Other Names:': formData.other_names,
          'Contents:': formData.contents,
          'Date:': formData.date,
          'Origin:': formData.place_of_origin,
          'Total Folia:': formData.total_folia,
          'Dimensions:': formData.dimensions,
          'Materials:': formData.materials,
          'Laod Folia:': formData.laod_folia,
          'Format Description:': formData.format_description
        },
        verses: verses
      }));

      // Add the image if it exists
      if (selectedImage) {
        // Convert base64 to blob
        const response = await fetch(selectedImage);
        const blob = await response.blob();
        formDataToSend.append('image', blob, 'manuscript_image.jpg');
      }

      const response = await fetch(`${API_BASE_URL}/api/documents/create/`, {
        method: 'POST',
        body: formDataToSend
      });

      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.error || 'Failed to submit manuscript');
      }

      toast({
        title: 'Manuscript submitted successfully',
        status: 'success',
        duration: 3000,
        isClosable: true,
      });

      // Clear form after successful submission
      handleClear();
    } catch (error) {
      toast({
        title: 'Error submitting manuscript',
        description: error instanceof Error ? error.message : 'Unknown error occurred',
        status: 'error',
        duration: 5000,
        isClosable: true,
      });
    } finally {
      setIsLoading(false);
    }
  }

  return (
    <Box>
      <NavigationBar />
      <Box>
        <Box bg="#08004F" py={8} px={6} position="relative">
          <Heading fontWeight="normal" ml={8} color="lightgray" size="lg">New Data Entry</Heading>
          <Flex position="absolute" right={6} top="50%" transform="translateY(-50%)" gap={4}>
            <Button 
              bg="#CB0606"
              color="white"
              _hover={{ bg: "#A80505" }}
              onClick={handleClear}
              borderRadius="full"
              size="md"
              px={8}
              isDisabled={isLoading}
            >
              Clear
            </Button>
            <Button 
              bg="#B8860B"
              color="white"
              _hover={{ bg: "#9A7B0A" }}
              onClick={handleSaveAsDraft}
              borderRadius="full"
              size="md"
              px={8}
              isLoading={isLoading}
              loadingText="Saving..."
            >
              Save as draft
            </Button>
            <Button 
              colorScheme="green" 
              onClick={handleComplete}
              borderRadius="full"
              size="md"
              px={8}
              isLoading={isLoading}
              loadingText="Submitting..."
            >
              Complete
            </Button>
          </Flex>
        </Box>

        <Box ml={8} p={6}>
          <Flex gap={12}>
            <Box flex={1}>
              <Flex mb={6} gap={6}>
                <Box flex={2}>
                  <Text fontSize="lg" mb={2}>MS ID</Text>
                  <Input 
                    name="ms_id"
                    value={formData.ms_id}
                    onChange={handleChange}
                    size="lg"
                    borderColor="gray.400"
                  />
                </Box>
                <Box flex={1}>
                  <Text fontSize="lg" mb={2}>Sigla</Text>
                  <Input 
                    name="sigla"
                    value={formData.sigla}
                    onChange={handleChange}
                    size="lg"
                    borderColor="gray.400"
                  />
                </Box>
                <Box flex={1}>
                  <Text fontSize="lg" mb={2}>Date</Text>
                  <Input 
                    name="date"
                    value={formData.date}
                    onChange={handleChange}
                    size="lg"
                    borderColor="gray.400"
                  />
                </Box>
              </Flex>

              <Flex mb={6} gap={6}>
                <Box flex={1}>
                  <Text fontSize="lg" mb={2}>Other Names</Text>
                  <Input 
                    name="other_names"
                    value={formData.other_names}
                    onChange={handleChange}
                    size="lg"
                    borderColor="gray.400"
                  />
                </Box>
                <Box flex={1}>
                  <Text fontSize="lg" mb={2}>Contents</Text>
                  <Input 
                    name="contents"
                    value={formData.contents}
                    onChange={handleChange}
                    size="lg"
                    borderColor="gray.400"
                  />
                </Box>
                <Box flex={1}>
                  <Text fontSize="lg" mb={2}>Place of Origin</Text>
                  <Input 
                    name="place_of_origin"
                    value={formData.place_of_origin}
                    onChange={handleChange}
                    size="lg"
                    borderColor="gray.400"
                  />
                </Box>
              </Flex>

              <Flex mb={6} gap={6}>
                <Box flex={1}>
                  <Text fontSize="lg" mb={2}>Total Folia</Text>
                  <Input 
                    name="total_folia"
                    value={formData.total_folia}
                    onChange={handleChange}
                    size="lg"
                    borderColor="gray.400"
                  />
                </Box>
                <Box flex={1}>
                  <Text fontSize="lg" mb={2}>Dimensions</Text>
                  <Input 
                    name="dimensions"
                    value={formData.dimensions}
                    onChange={handleChange}
                    size="lg"
                    borderColor="gray.400"
                  />
                </Box>
                <Box flex={1}>
                  <Text fontSize="lg" mb={2}>Materials</Text>
                  <Input 
                    name="materials"
                    value={formData.materials}
                    onChange={handleChange}
                    size="lg"
                    borderColor="gray.400"
                  />
                </Box>
                <Box flex={1}>
                  <Text fontSize="lg" mb={2}>Laod. Folia</Text>
                  <Input 
                    name="laod_folia"
                    value={formData.laod_folia}
                    onChange={handleChange}
                    size="lg"
                    borderColor="gray.400"
                  />
                </Box>
              </Flex>
            </Box>

            <Box flex={0.4} minW="300px">
              <input
                type="file"
                ref={fileInputRef}
                onChange={handleImageChange}
                accept="image/*"
                style={{ display: 'none' }}
              />
              <Box 
                border="2px dashed" 
                borderColor="gray.400" 
                borderRadius="md"
                height="300px"
                display="flex"
                alignItems="center"
                justifyContent="center"
                bg="gray.100"
                cursor="pointer"
                _hover={{ bg: "gray.200" }}
                onClick={handleImageClick}
                position="relative"
                overflow="hidden"
              >
                {selectedImage ? (
                  <Image
                    src={selectedImage}
                    alt="Selected manuscript"
                    objectFit="contain"
                    maxH="100%"
                    maxW="100%"
                  />
                ) : (
                  <VStack spacing={2}>
                    <Text color="gray.500" fontSize="lg">Upload image</Text>
                    <Text color="gray.400" fontSize="sm">Click to select a file</Text>
                  </VStack>
                )}
              </Box>
            </Box>
          </Flex>

          <Box mt={1}>
            <Text fontSize="lg" mb={2}>Format Description</Text>
            <Input 
              name="format_description"
              value={formData.format_description}
              onChange={handleChange}
              size="lg"
              borderColor="gray.400"
              width="100%"
            />
          </Box>

          <Box mt={6}>
            <Text fontSize="lg" mb={2}>Transcription</Text>
            <Text fontSize="sm" color="gray.600" mb={2}>
              Enter each verse on a new line. Press Enter/Return to start a new verse.
            </Text>
            <Textarea
              name="transcription"
              value={formData.transcription}
              onChange={handleChange}
              placeholder="Enter verses here...&#13;&#10;Press Enter/Return for each new verse"
              size="lg"
              height="500px"
              resize="vertical"
              borderColor="gray.400"
              width="100%"
              whiteSpace="pre-wrap"
            />
          </Box>
        </Box>
      </Box>
    </Box>
  );
}

export default NewDataEntry;
